class JSONParser(input: String) {

  val jsonVal: JsValue = Json.parse(input)

  def getFilterCategories(): Map[String, CategoryFilters] = {
    
    //Get Available Categories Filter
    val filtersJson = (jsonVal \ "available_filters" )  
    val filterScala = parse[List[CategoryFilters]](filtersJson.toString())
    val categories = filterScala.filter(_.id == "category" )(0)
    val states = filterScala.filter(_.id == "state" )(0)
    val priceRanges = filterScala.filter(_.id == "price")(0)
    Map("category" -> categories, "state" -> states, "price" -> priceRanges)
  }

  def getItems(): List[Item] = {
    val filtersJson = (jsonVal \ "results")
    parse[List[Item]](filtersJson.toString())
  }
